const files = $input.all();
let combinedText = "";

for (const file of files) {
    if (file.binary && file.binary.data) {
        // Extract the Base64 encoded text content
        const base64Text = file.binary.data.data;  

        // Decode Base64 to UTF-8 text
        const textData = Buffer.from(base64Text, "base64").toString("utf-8");  

        combinedText += textData + "\n\n";  // Merge files with spacing
    }
}

// Pass the decoded text to the next node
return [{ json: { chatInput: combinedText } }];
